import org.junit.Assert;
import org.junit.Test;

public class SolutionTest {

    @Test
    public void checkInclusionTest() {
        String s1 = "ab";
        String s2 = "eidbaooo";
        Assert.assertTrue(new Solution().checkInclusion(s1, s2));
        s1 = "ab";
        s2 = "eidboaoo";
        Assert.assertFalse(new Solution().checkInclusion(s1, s2));
        s1 = "";
        s2 = "";
        Assert.assertTrue(new Solution().checkInclusion(s1, s2));
    }

}
